import React, { useState } from 'react';

const CryptoPortfolioForm = () => {
  const [selectedOption, setSelectedOption] = useState('Kupovina');

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic here
    // You can use selectedOption for further processing
    console.log('Selected option:', selectedOption);
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-3">Dobrodošli u vaš Kripto Portfolio!</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="navigacija" className="form-label">Navigacija kripto portfolia:</label>
        <select id="navigacija" name="navigacija" value={selectedOption} onChange={handleOptionChange}>
          <option value="Kupovina">Kupovina</option>
          <option value="Prodaja">Prodaja</option>
          <option value="Prikaz">Prikaz portfolia</option>
        </select>
        <button type="submit" className="btn btn-primary">Idite na odabranu stranicu</button>
      </form>
    </div>
  );
};

export default CryptoPortfolioForm;
