import React, { useState, useEffect } from 'react';
 
const AddTransaction = () => {
   //stanje komponenti
    const [showModal, setShowModal] = useState(false);     //prati da li je prozor za unos transakcije trenutno prikazan ili ne
    const [transactionType, setTransactionType] = useState('');    //buy ili sell
    const [currencyName, setCurrencyName] = useState('');
    const [quantity, setQuantity] = useState('');
    const [pricePerCoin, setPricePerCoin] = useState('');
    const [date, setDate] = useState('');
    const [totalReceived, setTotalReceived] = useState('');
    const [error, setError] = useState(false);
 
   //kada kliknemo na dugme addtrans prikazuje prozor
    const handleAddTransaction = () => {
        setShowModal(true);
      };
    //tip transakcije buy ili sell
    const handleTransactionType = (type) => {
        setTransactionType(type);
      };
 
    const handleControllerResponse = () => {

    };
 
    const handleSaveTransaction = () => {   //prikupljanje podataka o transakciji, iz stanja komponenti koje neko unese
        const transactionData = {
          transactionType,
          currencyName,
          quantity,
          pricePerCoin,
          date,
          totalReceived,
        };
 
    //slanje zahtjeva na server
        fetch(`http://localhost:5000/${transactionType}`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',      //json format saljemo
            },
            body: JSON.stringify(transactionData),   //body sadrzi podatke koje saljemo na server iz js u json kako bi bio prihvacen od serv
          })
            .then((res) => res.json())      //rukovanje odgovorom od servera
            .then((data) => {
              if (data.success) {
                console.log('Transakcija uspešno sačuvana');
                // Resetuj stanje forme nakon uspešnog čuvanja
                setShowModal(false);
                setTransactionType('');
                setCurrencyName('');
                setQuantity('');
                setPricePerCoin('');
                setDate('');
                setTotalReceived('');
              } else {
                console.error('Greška prilikom čuvanja transakcije:', data.poruka);
                setError(true);
              }
            })
            .catch((error) => {
              console.error('Greška prilikom slanja zahteva:', error);
            });
        };
 
        useEffect(() => {
          // Implementirajte logiku za izračunavanje ukupno primljene vrednosti
          const total = parseFloat(quantity) * parseFloat(pricePerCoin);
          setTotalReceived(total.toFixed(2));
        }, [quantity, pricePerCoin]);
 
        return (
            <div>
              <button onClick={handleAddTransaction}>Add Transaction</button>
 
              {showModal && (
                <div>
                  <nav>
                    <button onClick={() => handleTransactionType('buy')}>Buy</button>
                    <button onClick={() => handleTransactionType('sell')}>Sell</button>
                  </nav>
 
                  <form>
                    <label>Currency Name:</label>
                    <input
                      type="text"
                      value={currencyName}
                      onChange={(e) => setCurrencyName(e.target.value)}
                    />
 
                    <label>Quantity:</label>
                    <input
                      type="text"
                      value={quantity}
                      onChange={(e) => setQuantity(e.target.value)}
                    />
 
                    <label>Price per Coin:</label>
                    <input
                      type="text"
                      value={pricePerCoin}
                      onChange={(e) => setPricePerCoin(e.target.value)}
                    />
 
                    <label>Date:</label>
                    <input
                      type="date"
                      value={date}
                      onChange={(e) => setDate(e.target.value)}
                    />
 
                    <label>Total Received:</label>
                    <input
                      type="text"
                      value={totalReceived}
                      readOnly
                    />
 
                    <button type="button" onClick={handleSaveTransaction}>    //mozemo postaviti da ide na drugu stranicu
                      Save Transaction
                    </button>
                  </form>
                </div>
              )}
 
              <div>
                {/* Kontroleri za kupovinu i prodaju */}
                <button onClick={() => fetch('http://localhost:5000/kupovina').then(handleControllerResponse)}>
                  Kupovina
                </button>
                <button onClick={() => fetch('http://localhost:5000/prodaja').then(handleControllerResponse)}>
                  Prodaja
                </button>
              </div>
 
              {/* Dugme koje vodi na drugu stranicu (postavite pravu putanju) */}
              <button onClick={() => window.location.href = '/userhome'}>
                Go to Another Page
              </button>
            </div>
          );
        };
 
export default AddTransaction;
