import React, { useState, useEffect } from 'react';

const HomeUserPage = () => {
  const [userData, setUserData] = useState(null);
  const [edit, setEdit] = useState(false);
  const [newData, setNewData] = useState({});
  const email_old = localStorage.getItem('user_email');
  useEffect(() => {
    // Function to fetch user data
    const storedUserData = localStorage.getItem('user_email');
    console.log(storedUserData)
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/userhome', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({storedUserData}),
    });
    if (response.ok) {
      const data = await response.json();
      setUserData(data);
    } else {
      console.error(`Failed to fetch user data. Status: ${response.status}`);
    }
  } catch (error) {
    console.error('Error fetching user data:', error);
  }
    };

   
    fetchUserData();
  }, []); 

  const handleEditClick = () => {
    setEdit(true);
  };

  const handleChange = (e) => {
    setNewData({ ...newData, [e.target.name]: e.target.value });
  }; 

  const handleCancelClick = () => {
    setEdit(false);
    setNewData({
      name: userData.name,
      surname: userData.surname,
      address: userData.address,
      city: userData.city,
      country: userData.country,
      phoneNumber: userData.phone_number,
      email: userData.email,
      password: '',
      newPassword: '',
    });
  };

  const handleUpdateClick = () => {
    const data = {
      firstName:newData.name || userData.name,
      lastName: newData.surame || userData.surname,
      address: newData.address || userData.adress,
      city: newData.city || userData.city,
      country: newData.country || userData.country,
      phoneNumber:newData.phoneNumber || userData.phone_number,
      email: newData.email || userData.email,
      password: newData.password || '',
      newPassword: newData.newPassword || '',
      oldEmail: email_old,
    };

    fetch('http://localhost:8000/updateUser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((new_data) => {
        if (responseData.message === 'Vasi podaci uspesno izmenjeni!') {
          setMessage(responseData.message);
          localStorage.setItem('user', JSON.stringify(new_data.user));
          setUser(new_data.user);
          setEditMode(false);
        } else {
          setMessage(responseData.message);
        }
      })
      .catch((error) => {
        console.error("Fetch Error:", error);
      });
  };

  return (
    <div>
      <h1 >Dobrodošli u vaš Kripto Portfolio!</h1>
      {userData ? (
        <div>
          <p>User ID: {userData.id}</p>
          <p>Name: {userData.name}</p>
          <p>Surname: {userData.surname}</p>
          <p>Email: {userData.email}</p>
          <button onClick={handleEditClick}>Izmjeni</button>
        </div>
        
      ) : (
        <p>Loading user data...</p>
      )}
   
    {
      edit && (
        <form className='update-form'> 
        <table>
          <tbody>
            <tr>
              <td><label>Ime:
              <input
                type='text'
                name='name'
                value={newData.name || userData.name}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Prezime:
              <input
                type='text'
                name='lastname'
                value={newData.surname || userData.surname} 
                onChange={handleChange}
                />
              </label></td>
            </tr>
            <tr>
              <td>
            <label>
              Adresa:
              <input
                type='text'
                name='address'
                value={newData.address || userData.address}
                onChange={handleChange}
              />
              </label>
              </td>
            </tr>
            <tr>
              <td><label>Grad:
              <input
                type='text'
                name='city'
                value={newData.city ||userData.city}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Država:
              <input
                type='text'
                name='country'
                value={newData.country || userData.country}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Broj telefona:
              <input
                type='text'
                name='phoneNumber'
                value={newData.phoneNumber || userData.phone_number}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Email:
              <input
                type='text'
                name='email'
                value={newData.email || userData.email}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Lozinka:
              <input
                type='password'
                name='password'
                value={newData.password || ''}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Nova lozinka:
              <input
                type='password'
                name='newPassword'
                value={newData.newPassword || ''}
                onChange={handleChange}
              /></label></td>
            </tr>
          </tbody>
        </table>
        <p>
  <button type='button' onClick={handleCancelClick}>ODUSTANI</button>
  <button type='button' onClick={handleUpdateClick}>IZMENI</button>
  </p>
     </form> 
      )
    }
    </div>
  );
};

export default HomeUserPage;
