          <button onClick={profitHandle}>Show financials</button>
          <button onClick={addNewTransaction}>Add new transaction</button>
        </div>
        
      ) : (
        <p>Loading user data...</p>
      )}
   
    {
      edit && (
        <form className='update-form'> 
        <table>
          <tbody>
            <tr>
              <td><label>Ime:
              <input
                type='text'
                name='name'
                value={newData.name || userData.name}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Prezime:
              <input
                type='text'
                name='surname'
                value={newData.surname || userData.surname} 
