    </div>
    <div>
      {cryptoData2 && (
        <table>
          <thead>
            <tr>
              <th>Crypto Currency</th>
              <th>Current Price</th>
              <th>Timestamp</th>
             
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{cryptoData2.cryptoCurrency2}</td>
              <td>{cryptoData2.currentPrice2}</td>
