import React, { useState, useEffect } from 'react';

const HomeUserPage = () => {
  const [userData, setUserData] = useState(null);
  const [edit, setEdit] = useState(false);
  const [newData, setNewData] = useState({});
  useEffect(() => {
    // Function to fetch user data
    const storedUserData = localStorage.getItem('user_email');
    console.log(storedUserData)
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/userhome', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({storedUserData}),
    });
    if (response.ok) {
      const data = await response.json();
      setUserData(data);
    } else {
      console.error(`Failed to fetch user data. Status: ${response.status}`);
    }
  } catch (error) {
    console.error('Error fetching user data:', error);
  }
    };

   
    fetchUserData();
  }, []); 

  const handleEditClick = () => {
    setEdit(true);
  };

  const handleCancelClick = () => {
    setEdit(false);
    setNewData({
      name: userData.name,
      surname: userData.surname,
      address: userData.address,
      city: userData.city,
      country: userData.country,
      phoneNumber: userData.phoneNumber,
      email: userData.email,
      password: '',
      newPassword: '',
    });
  };

  const handleUpdateClick = () => {

  };

  return (
    <div>
      <h1 >Dobrodošli u vaš Kripto Portfolio!</h1>
      {userData ? (
        <div>
          <p>User ID: {userData.id}</p>
          <p>Name: {userData.name}</p>
          <p>Surname: {userData.surname}</p>
          <p>Email: {userData.email}</p>
          <button onClick={handleEditClick}>Izmjeni</button>
        </div>
        
      ) : (
        <p>Loading user data...</p>
      )}
   
    {
      edit && (
        <form className='update-form'> 
        <table>
          <tbody>
            <tr>
              <td><label>Ime:
              <input
                type='text'
                name='name'
                value={newData.name || userData.name}
                
              /></label></td>
            </tr>
            <tr>
              <td><label>Prezime:
              <input
                type='text'
                name='lastname'
                //value={editUserData.lastName || userData.surname} 
                />
              </label></td>
            </tr>
            <tr>
              <td>
            <label>
              Adresa:
              <input
                type='text'
                name='address'
                //value={editUserData.address || userData.address}
              
              />
              </label>
              </td>
            </tr>
            <tr>
              <td><label>Grad:
              <input
                type='text'
                name='city'
                //value={editUserData.city ||userData.city}
            
              /></label></td>
            </tr>
            <tr>
              <td><label>Država:
              <input
                type='text'
                name='country'
                //value={editUserData.country || userData.country}
                
              /></label></td>
            </tr>
            <tr>
              <td><label>Broj telefona:
              <input
                type='text'
                name='phoneNumber'
                //value={editUserData.phoneNumber || userData.phoneNumber}
             
              /></label></td>
            </tr>
            <tr>
              <td><label>Email:
              <input
                type='text'
                name='email'
                //value={editUserData.email || userData.email}
                
              /></label></td>
            </tr>
            <tr>
              <td><label>Lozinka:
              <input
                type='password'
                name='password'
                //value={editUserData.password || ''}
                
              /></label></td>
            </tr>
            <tr>
              <td><label>Nova lozinka:
              <input
                type='password'
                name='newPassword'
               // value={editUserData.newPassword || ''}
               
              /></label></td>
            </tr>
          </tbody>
        </table>
        <p>
  <button type='button' onClick={handleCancelClick}>ODUSTANI</button>
  <button type='button' onClick={handleUpdateClick}>IZMENI</button>
  </p>
     </form> 
      )
    }
    </div>
  );
};

export default HomeUserPage;
