import React, { useState, useEffect } from 'react';

const HomeUserPage = () => {
  const [userData, setUserData] = useState({});
  const [edit, setEdit] = useState(false);
  const [editCrypto, setEditCrypto] = useState(false);
  const [newData, setNewData] = useState({});
  const [message, setMessage] = useState('');
  const [user, setUser] = useState('');
  const [cryptoData, setCryptoData] = useState(null);
  const [cryptoData1, setCryptoData1] = useState(null);
  const [cryptoData2, setCryptoData2] = useState(null);
  const [currentDateTime, setCurrentDateTime] = useState(new Date());
  const [cryptoValues, setCryptoValues] = useState(null);
  const [products, setProducts] = useState({});
  
  const email_old = localStorage.getItem('user_email');
  useEffect(() => {
    // Function to fetch user data
    const storedUserData = localStorage.getItem('user_email');
    console.log(storedUserData)
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/userhome', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({storedUserData}),
    });
    if (response.ok) {
      const data = await response.json();
      setUserData(data);
    } else {
      console.error(`Failed to fetch user data. Status: ${response.status}`);
    }
  } catch (error) {
    console.error('Error fetching user data:', error);
  }
    };

   
    fetchUserData();
  }, []); 

  const fetchDataHandle = () => {
    fetch(`http://localhost:5000/getBTC`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',      //json format saljemo
            },
            body: JSON.stringify(),   //body sadrzi podatke koje saljemo na server iz js u json kako bi bio prihvacen od serv
          })
          .then((res) => res.json())      //rukovanje odgovorom od servera
            .then((data) => {
              if (data.success) {
                const cryptoCurrency = data.crypto.pair.split(':')[0];
                const currentPrice = parseFloat(data.crypto.last);
                const timestamp = new Date(parseInt(data.crypto.timestamp) * 1000);

        // Storing the extracted information in state
        setCryptoData({
          cryptoCurrency,
          currentPrice,
          timestamp,
        });

              } else {
               
              }
            })
            .catch((error) => {
              console.error('Error fetching crypto data:', error);
            });

    fetch('http://localhost:5000/getETH', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',      //json format saljemo
        },
        body: JSON.stringify(),
      })
      .then((res) => res.json())      //rukovanje odgovorom od servera
      .then((data) => {
        if (data.success) {
          console.log(data.crypto)
          const cryptoCurrency1 = data.crypto.pair.split(':')[0];
          const currentPrice1 = parseFloat(data.crypto.last);
          const timestamp1 = new Date(parseInt(data.crypto.timestamp) * 1000);
          setCryptoData1({
            cryptoCurrency1,
            currentPrice1,
            timestamp1,
          });
          } else {

          }
        })
        .catch((error) => {
          console.error('Error fetching crypto data:', error);
        });

        fetch('http://localhost:5000/getLTC', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',      //json format saljemo
          },
          body: JSON.stringify(),
        })
        .then((res) => res.json())      //rukovanje odgovorom od servera
        .then((data) => {
          if (data.success) {
            const cryptoCurrency2 = data.crypto.pair.split(':')[0];
            const currentPrice2 = parseFloat(data.crypto.last);
            const timestamp2 = new Date(parseInt(data.crypto.timestamp) * 1000);
            setCryptoData2({
              cryptoCurrency2,
              currentPrice2,
              timestamp2,
            });
            } else {
  
            }
          })
          .catch((error) => {
            console.error('Error fetching crypto data:', error);
          });
          setEditCrypto(true)
        }
  const handleEditClick = () => {
    setEdit(true);
  };

  const handleChange = (e) => {
    setNewData({ ...newData, [e.target.name]: e.target.value });
  }; 

  const closeHandle = () => {
    setCryptoData(null)
    setCryptoData1(null)
    setCryptoData2(null)
  };

  const handleCancelClick = () => {
    setEdit(false);
    setNewData({
      name: userData.name,
      surname: userData.surname,
      address: userData.address,
      city: userData.city,
      country: userData.country,
      phoneNumber: userData.phone_number,
      email: userData.email,
      password: '',
      newPassword: '',
    });
    console.log(userData.name);
  };

  const showHandle = () =>{
    fetch('http://localhost:5000/showCrypto',{
      method: 'POST',
            headers: {
              'Content-Type': 'application/json',      //json format saljemo
            },
            body: JSON.stringify({email_old}),
    })
    .then((res) => res.json())      //rukovanje odgovorom od servera
      .then((data) => {
        if (data.success){
          console.log("USAO OVDE");
              setProducts(data.users)
              const counter = data.counter;
              const bitcoin = data.b;
              const ethereum = data.e;
              const litecoin = data.l;
              setCryptoValues({
                counter,
                bitcoin,
                ethereum,
                litecoin
              });
        }
        else {
          console.error("No crypto data\n");
        }
      })
      .catch((error) => {
        console.error('Error fetching crypto data:', error);
      });

  } 

  const BuyBitcoinHandle  = () => {
    setCurrentDateTime(new Date());
    const buy_data={
      cryptoCurrency: cryptoData.cryptoCurrency,
      currentPrice : cryptoData.currentPrice,
      timestamp : `${currentDateTime.getFullYear()}-${(currentDateTime.getMonth() + 1)
        .toString()
        .padStart(2, '0')}-${currentDateTime.getDate().toString().padStart(2, '0')} ${currentDateTime
        .getHours()
        .toString()
        .padStart(2, '0')}:${currentDateTime.getMinutes().toString().padStart(2, '0')}:${currentDateTime
        .getSeconds()
        .toString()
        .padStart(2, '0')}`,
      email : email_old

    }
    
    fetch('http://localhost:5000/buyCrypto', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',      //json format saljemo
          },
          body: JSON.stringify(buy_data),
        })
        .then((res) => res.json())      //rukovanje odgovorom od servera
        .then((data) => {
          if (data.success) {
              console.log(data.message)

            } else {
              console.log(data.message)
            }
          })
          .catch((error) => {
            console.error('Error fetching crypto data:', error);
          });
  };

  const buyEtheriumHandle  = () => {
    setCurrentDateTime(new Date());
    const buy_data1={
      cryptoCurrency: cryptoData1.cryptoCurrency1,
      currentPrice : cryptoData1.currentPrice1,
      timestamp : `${currentDateTime.getFullYear()}-${(currentDateTime.getMonth() + 1)
        .toString()
        .padStart(2, '0')}-${currentDateTime.getDate().toString().padStart(2, '0')} ${currentDateTime
        .getHours()
        .toString()
        .padStart(2, '0')}:${currentDateTime.getMinutes().toString().padStart(2, '0')}:${currentDateTime
        .getSeconds()
        .toString()
        .padStart(2, '0')}`,
      email : email_old

    }
    
    fetch('http://localhost:5000/buyCrypto', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',      //json format saljemo
          },
          body: JSON.stringify(buy_data1),
        })
        .then((res) => res.json())      //rukovanje odgovorom od servera
        .then((data) => {
          if (data.success) {
              console.log(data.message)

            } else {
              console.log(data.message)
            }
          })
          .catch((error) => {
            console.error('Error fetching crypto data:', error);
          });
  };

  const buyLitecoinHandle = () => {
    setCurrentDateTime(new Date());
    const buy_data2={
      cryptoCurrency: cryptoData2.cryptoCurrency2,
      currentPrice : cryptoData2.currentPrice2,
      timestamp : `${currentDateTime.getFullYear()}-${(currentDateTime.getMonth() + 1)
        .toString()
        .padStart(2, '0')}-${currentDateTime.getDate().toString().padStart(2, '0')} ${currentDateTime
        .getHours()
        .toString()
        .padStart(2, '0')}:${currentDateTime.getMinutes().toString().padStart(2, '0')}:${currentDateTime
        .getSeconds()
        .toString()
        .padStart(2, '0')}`,
      email : email_old

    }
    
    fetch('http://localhost:5000/buyCrypto', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',      //json format saljemo
          },
          body: JSON.stringify(buy_data2),
        })
        .then((res) => res.json())      //rukovanje odgovorom od servera
        .then((data) => {
          if (data.success) {
              console.log(data.message)

            } else {
              console.log(data.message)
            }
          })
          .catch((error) => {
            console.error('Error fetching crypto data:', error);
          });
  };

  const handleUpdateClick = async () => {
    const data = {
      name:newData.name || userData.name,
      surname: newData.surname || userData.surname,
      address: newData.address || userData.address,
      city: newData.city || userData.city,
      country: newData.country || userData.country,
      phoneNumber:newData.phoneNumber || userData.phone_number,
      password: newData.password || '',
      newPassword: newData.newPassword || userData.password,
      oldEmail: email_old,
    };
    console.log(data.address);
    try {
      const response = await fetch('http://localhost:5000/edit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
    }
    
      if (response.ok) {
        const new_data = await response.json();
        console.log("ovde je uslo");
        setUserData(new_data.user);
        setEdit(false);
      } else {
        const new_data = await response.json();
        setMessage(new_data.message);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };
  
  return (
    <div>
      <h1 >Dobrodošli u vaš Kripto Portfolio!</h1>
      {userData ? (
        <div>
          <p>User ID: {userData.id}</p>
          <p>Name: {userData.name}</p>
          <p>Surname: {userData.surname}</p>
          <p>Email: {userData.email}</p>
          <button onClick={handleEditClick}>Izmjeni</button>
          <button onClick={fetchDataHandle}>Add new crypto currency</button>
          <button onClick={showHandle}>Show crytpto wallet</button>
        </div>
        
      ) : (
        <p>Loading user data...</p>
      )}
   
    {
      edit && (
        <form className='update-form'> 
        <table>
          <tbody>
            <tr>
              <td><label>Ime:
              <input
                type='text'
                name='name'
                value={newData.name || userData.name}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Prezime:
              <input
                type='text'
                name='surname'
                value={newData.surname || userData.surname} 
                onChange={handleChange}
                /></label></td>
            </tr>
            <tr>
              <td>
            <label>
              Adresa:
              <input
                type='text'
                name='address'
                value={newData.address || userData.address}
                onChange={handleChange}
              />
              </label>
              </td>
            </tr>
            <tr>
              <td><label>Grad:
              <input
                type='text'
                name='city'
                value={newData.city ||userData.city}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Država:
              <input
                type='text'
                name='country'
                value={newData.country || userData.country}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Broj telefona:
              <input
                type='text'
                name='phoneNumber'
                value={newData.phoneNumber || userData.phone_number}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Lozinka:
              <input
                type='password'
                name='password'
                value={newData.password || ''}
                onChange={handleChange}
              /></label></td>
            </tr>
            <tr>
              <td><label>Nova lozinka:
              <input
                type='password'
                name='newPassword'
                value={newData.newPassword || ''}
                onChange={handleChange}
              /></label></td>
            </tr>
          </tbody>
        </table>
        <p>
  <button type='button' onClick={handleCancelClick}>ODUSTANI</button>
  <button type='button' onClick={handleUpdateClick}>IZMENI</button>
  </p>
     </form> 
      )
    }
    <div>
      
      {cryptoData && (
        
        <table>
          <thead>
            <tr>
              <th>Crypto Currency</th>
              <th>Current Price</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{cryptoData.cryptoCurrency}</td>
              <td>{cryptoData.currentPrice}</td>
              <td>{cryptoData.timestamp.toString()}</td>
            </tr>
            <td><button onClick={BuyBitcoinHandle}>Buy</button></td>
          </tbody>
        </table>
      )}
    </div>
    <div>
      {cryptoData1 && (
        <table>
          <thead>
            <tr>
              <th>Crypto Currency</th>
              <th>Current Price</th>
              <th>Timestamp</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{cryptoData1.cryptoCurrency1}</td>
              <td>{cryptoData1.currentPrice1}</td>
              <td>{cryptoData1.timestamp1.toString()}</td>
            </tr>
          </tbody>
          <button onClick={buyEtheriumHandle}>Buy</button>
        </table>
      )}
    </div>
    <div>
      {cryptoData2 && (
